class App extends Component {
  state = {
    title: "",
    description: ""
  };

  onSubmit = e => {
    e.preventDefault();
    console.log(this.state);
  };
  render() {
    return (
      <div className="App">
        {/* <Navbar bg="light" expand="lg">
